int n , a[N] , f[N] , rd[N];
int main()
{
    cin >> n; for (int i = 1; i <= n; i++){cin >> a[i]; rd[a[i]]++;}
    for (int i = 1; i <= n; i++){f[i] = 1;}
    queue <int> q; for (int i = 1; i <= n; i++){if (!rd[i]){q.push(i);}}
    while(!q.empty()) {
        int u = q.front(); q.pop();
        if (f[u]){f[a[u]] = 0;}
        rd[a[u]]--;
        if (!rd[a[u]]) {
            q.push(a[u]);
        }
