            do {
                t = a[t];
                if (x != f[t]) {
                    if (x == 1){x = f[t];}
                    else {f[t] = x;}
                } rd[t]--; x ^= 1;
            } while(t != i);
        }
    }
    for (int i = 1; i <= n; i++) {
